    doc.setFontSize(18);
    doc.text(`${title} Emergency Kit`, 15, 20);
    doc.setFontSize(12);
    let y = 40;
    kitItems.forEach((item) => { doc.text(` ${item}`, 15, y); y += 10; });
    doc.save(`${title.replace(" ", "_")}_Kit.pdf`);
